INSERT INTO `kategoriler` (`id`, `kate_adi`, `kate_turu`, `aciklama`) VALUES
(11, 'Fitness', 'ustkategori', 'Fitness sağlıklı olmak, formda olmak anlamlarını içerir. Sağlık ve zinde olmak adına yapılacak olan çok çeşitli spor dallarını, bünyesinde barındırır.'),
(13, 'Pilates', 'ustkategori', 'Pilates, eklem ve kemikleri korumak için kas güçlendiren ve içsel karın kaslarının kuvvetlendirilmesi esasına dayanan bir sistemdir.'),
(14, 'Kick Boks', 'ustkategori', 'Kick boks, güç ve tecrübeye dayanan yumruk ve tekme teknikleriyle iki rakip arasında yapılan dövüş sporudur.');

